function modal(){return{template:'<div class="modal fade"><div class="modal-dialog"><div class="modal-content"><div class="modal-header"><button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button><h4 class="modal-title">{{ buttonClicked }} !!</h4></div><div class="modal-body" ng-transclude></div></div></div></div>',restrict:"E",transclude:!0,replace:!0,scope:!0,link:function(a,d,i){a.$watch(i.visible,function(a){a===!0?$(d).modal("show"):$(d).modal("hide")}),$(d).on("shown.bs.modal",function(){a.$apply(function(){a.$parent[i.visible]=!0})}),$(d).on("hidden.bs.modal",function(){a.$apply(function(){a.$parent[i.visible]=!1})})}}}angular.module("bilancio").directive("modal",modal);